INSERT INTO "Knowledge__kav" VALUES(303,'Education Level','Education-Level','If you are an American citizen, you are eligible for aid if you have received a high school diploma, received a General Educational Development (GED) certificate, or completed a high school education in a homeschool setting approved under law for your state.');
INSERT INTO "Knowledge__kav" VALUES(304,'Homeschool Eligibility','Homeschool-Eligibility','Different states have different education requirements for homeschooling. In Nebraska and Vermont, education requirements come from local school boards. For information relevant to your state, please visit https://www.homeschool.com/articles/high-school-graduation-requirements-by-state/');
INSERT INTO "Knowledge__kav" VALUES(305,'Approved use of money','Approved-use-of-money','When you complete the FAFSA, you sign a statement to certify you will use any money you get from federal student aid for educational purposes only and that you do not already owe money, due to a grant refund or another federal student loan going into default. This statement is in Step Seven');
INSERT INTO "Knowledge__kav" VALUES(306,'1','fafsa-question-1','Enter in your last name. This is your proper name, not a nickname, and it must match exactly the name on your Social Security card.');
INSERT INTO "Knowledge__kav" VALUES(307,'2','fafsa-question-2','Enter in your first name. This is your proper name, not a nickname, and it must match exactly the name on your Social Security card.');
